import { Routes, Route, Navigate } from "react-router-dom";
import Sidebar from "../components/Sidebar";
import AssignTask from "../components/AssignTask";
import TaskList from "../components/TaskList";
import CompletedTasks from "../components/CompletedTasks";
import Profile from "../components/profile";
import { jwtDecode } from "jwt-decode";

function Dashboard() {
  let role = "user";
  const token = localStorage.getItem("token");
  if (token) {
    const decoded = jwtDecode(token);
    role = decoded.role;
  }

  return (
    <div style={{ display: "flex" }}>
      <Sidebar />
      <div className="dashboard-content">
        <Routes>
          <Route path="/" element={<h2>Welcome to Dashboard!</h2>} />
          {role === "admin" && <Route path="/assign" element={<AssignTask />} />}
          <Route path="/tasks" element={<TaskList />} />
          <Route path="/completed" element={<CompletedTasks />} />
          <Route path="/profile" element={<Profile />} />
          {/* If user tries to go to /assign manually, block them */}
          {role !== "admin" && <Route path="/assign" element={<Navigate to="/dashboard/tasks" />} />}
        </Routes>
      </div>
    </div>
  );
}

export default Dashboard;
