import { useState, useEffect } from "react";
import axios from "axios";

function TaskList() {
  const [tasks, setTasks] = useState([]);

  const fetchTasks = async () => {
    try {
      const token = localStorage.getItem("token");

      const res = await axios.get("http://localhost:5000/api/tasks/get", {
        headers: { Authorization: token }
      });

      setTasks(res.data);
    } catch (err) {
      console.error(err.response?.data || err.message);
      alert("Failed to fetch tasks.");
    }
  };

  const completeTask = async (taskId) => {
    try {
      const token = localStorage.getItem("token");
      await axios.put(`http://localhost:5000/api/tasks/complete/${taskId}`, {}, {
        headers: { Authorization: token }
      });

      alert("Task marked as Completed!");
      fetchTasks(); // Refresh tasks after completing
    } catch (err) {
      console.error(err.response?.data || err.message);
      alert("Failed to complete task.");
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  return (
    <div style={{ margin: "20px" }}>
      <h2>Task List</h2>
      <table border="1" cellPadding="10" style={{ width: "100%", borderCollapse: "collapse" }}>
        <thead>
          <tr>
            <th>Title</th>
            <th>Description</th>
            <th>Assigned To</th>
            <th>Status</th>
            <th>Document</th>
            <th>Actions</th> {/* ðŸ‘ˆ NEW */}
          </tr>
        </thead>
        <tbody>
          {tasks.length > 0 ? (
            tasks.map(task => (
              <tr key={task._id}>
                <td>{task.title}</td>
                <td>{task.description}</td>
                <td>{task.assignedTo}</td>
                <td>{task.status}</td>
                <td>
                  {task.document ? (
                    <a
                      href={`http://localhost:5000/uploads/${task.document}`}
                      target="_blank"
                      rel="noreferrer"
                    >
                      View
                    </a>
                  ) : (
                    "No Document"
                  )}
                </td>
                <td>
                  {task.status !== "Completed" ? (
                    <button onClick={() => completeTask(task._id)}>Complete</button>
                  ) : (
                    <span style={{ color: "green" }}>Done</span>
                  )}
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="6" style={{ textAlign: "center" }}>No Tasks Available</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}

export default TaskList;
