import { useState, useEffect } from "react";
import axios from "axios";

function TaskList() {
  const [tasks, setTasks] = useState([]);

  const fetchTasks = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get("http://localhost:5000/api/tasks/get", {
        headers: { Authorization: token }
      });
      setTasks(res.data);
    } catch (err) {
      console.error(err.response?.data || err.message);
      alert("Failed to fetch tasks.");
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  const completeTask = async (id) => {
    try {
      const token = localStorage.getItem("token");
      await axios.put(`http://localhost:5000/api/tasks/complete/${id}`, {}, {
        headers: { Authorization: token }
      });
      alert("Task marked as completed!");
      fetchTasks();
    } catch (err) {
      console.error(err.response?.data || err.message);
      alert("Failed to complete task.");
    }
  };

  return (
    <div style={{ margin: "20px" }}>
      <h2>Task List</h2>
      <table border="1" cellPadding="10">
        <thead>
          <tr>
            <th>Title</th>
            <th>Description</th>
            <th>Assigned To</th>
            <th>Status</th>
            <th>Document</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {tasks.map(task => (
            <tr key={task._id}>
              <td>{task.title}</td>
              <td>{task.description}</td>
              <td>{task.assignedTo}</td>
              <td>{task.status}</td>
              <td>
                {task.document && (
                  <a href={`http://localhost:5000/uploads/${task.document}`} target="_blank" rel="noreferrer">
                    View
                  </a>
                )}
              </td>
              <td>
                {task.status !== "Completed" && (
                  <button onClick={() => completeTask(task._id)}>Mark Completed</button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default TaskList;
