import { useState, useEffect } from "react";
import axios from "axios";

function TaskList() {
  const [tasks, setTasks] = useState([]);
  const [selectedTaskId, setSelectedTaskId] = useState(null);
  const [action, setAction] = useState("");

  const fetchTasks = async () => {
    try {
      const token = localStorage.getItem("token");

      const res = await axios.get("http://localhost:5000/api/tasks/get", {
        headers: { Authorization: token }
      });

      setTasks(res.data);
    } catch (err) {
      console.error(err.response?.data || err.message);
      alert("Failed to fetch tasks.");
    }
  };

  const completeTask = async (taskId) => {
    try {
      const token = localStorage.getItem("token");
      await axios.put(`http://localhost:5000/api/tasks/complete/${taskId}`, {}, {
        headers: { Authorization: token }
      });

      alert("Task marked as Completed!");
      fetchTasks();
    } catch (err) {
      console.error(err.response?.data || err.message);
      alert("Failed to complete task.");
    }
  };

  const updateStatus = async () => {
    try {
      const token = localStorage.getItem("token");
      await axios.post(`http://localhost:5000/api/tasks/history/${selectedTaskId}`, {
        action
      }, {
        headers: { Authorization: token }
      });

      alert("Status updated!");
      setSelectedTaskId(null);
      setAction("");
      fetchTasks();
    } catch (err) {
      console.error(err.response?.data || err.message);
      alert("Failed to update status.");
    }
  };

  const viewTimeline = async (taskId) => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get(`http://localhost:5000/api/tasks/history/${taskId}`, {
        headers: { Authorization: token }
      });

      const history = res.data;
      let message = "Task Timeline:\n\n";

      history.forEach(item => {
        message += `[${new Date(item.timestamp).toLocaleString()}] - ${item.action} by ${item.by}\n`;
      });

      alert(message);
    } catch (err) {
      console.error(err.response?.data || err.message);
      alert("Failed to fetch history.");
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  return (
    <div style={{ margin: "20px" }}>
      <h2>Task List</h2>
      <table border="1" cellPadding="10" style={{ width: "100%", borderCollapse: "collapse" }}>
        <thead>
          <tr>
            <th>Title</th>
            <th>Description</th>
            <th>Assigned To</th>
            <th>Status</th>
            <th>Document</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {tasks.length > 0 ? (
            tasks.map(task => (
              <tr key={task._id}>
                <td>{task.title}</td>
                <td>{task.description}</td>
                <td>{task.assignedTo}</td>
                <td>{task.status}</td>
                <td>
                  {task.document ? (
                    <a
                      href={`http://localhost:5000/uploads/${task.document}`}
                      target="_blank"
                      rel="noreferrer"
                    >
                      View
                    </a>
                  ) : (
                    "No Document"
                  )}
                </td>
                <td>
                  {task.status !== "Completed" ? (
                    <button onClick={() => completeTask(task._id)}>Complete</button>
                  ) : (
                    <span style={{ color: "green" }}>Done</span>
                  )}
                  <br />
                  <button onClick={() => setSelectedTaskId(task._id)}>Update Status</button>
                  <br />
                  <button onClick={() => viewTimeline(task._id)}>View Timeline</button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="6" style={{ textAlign: "center" }}>No Tasks Available</td>
            </tr>
          )}
        </tbody>
      </table>

      {selectedTaskId && (
        <div style={{ marginTop: "20px", padding: "10px", border: "1px solid gray" }}>
          <h3>Update Status for Task</h3>
          <select value={action} onChange={(e) => setAction(e.target.value)}>
            <option value="">Select an action</option>
            <option value="Document Requested">Document Requested</option>
            <option value="Task Approved">Task Approved</option>
            <option value="Task Rejected">Task Rejected</option>
            <option value="Additional Documents Required">Additional Documents Required</option>
          </select>
          <br /><br />
          <button onClick={updateStatus}>Submit Status Update</button>
          <button onClick={() => { setSelectedTaskId(null); setAction(""); }} style={{ marginLeft: "10px" }}>Cancel</button>
        </div>
      )}
    </div>
  );
}

export default TaskList;
